import React, {Component} from 'react';



class Painting extends Component{

    render(props){
        return (<div>
                <h2>{props.painting.title}</h2>
                <img src={props.painting.image}></img>
                 <p>{props.painting.artist.name}</p>
              </div>)
    }

}


