import React, {Component} from 'react';



class Painting extends Component{
    constructor(props){
        super()
          this.state = {
            text: props.title
          }
      }

    render(){
        return (<div>
                <h2>{this.props.painting.title}</h2>
                <img src={this.props.painting.image}></img>
                 <p>{this.props.painting.artist.name}</p>
              </div>)
    }

}


