class App extends Component{

  constructor(){
    super()
      this.state = {
        list: true,
        paintings: paintings
      }
  }

  // state = {
  //   text: '040119'
  // }


  changeList = () => {
    console.log('I am inside ChangeList function')
    // console.log(this.state)
    this.setState({
      list: !this.state.list
      //list: false
    })
  }

  render(){
    // console.log(this.state.text)
    return (<div>
      <Header changeList={this.changeList}/>
      {this.state.list ?  <PaintingList paintings={paintings} /> : <PaintingForm />}

    </div>
    )
  }

}

export default App;
